INSERT INTO `books_olgainfo` VALUES (1,'ÐµÐµÐµ','1','1','1','1'),(2,'Interpreter and translator','<p>Offering interpreting on seminars, events, business meetings, conferences</p>\r\n<p><strong>Experience in International events:</strong></p>\r\n<ul>\r\n<li>Railway road summit in Rome</li>\r\n<li>Conferences on medical, legal and financial subjects</li>\r\n<li>Fashion events</li>\r\n<li>Business meetings</li>\r\n</ul>\r\n<div><strong>Interpreting</strong></div>\r\n<ul>\r\n<li>synchro interpreting</li>\r\n<li>consequtive interpreting</li>\r\n<li>written translation</li>\r\n</ul>\r\n<p><strong>Languages: </strong></p>\r\n<ul>\r\n<li>Italian</li>\r\n<li>English</li>\r\n</ul>\r\n<p><strong>Rates:</strong></p>\r\n<ul>\r\n<li><strong>Consequtive translation</strong> - 60 euro per hour</li>\r\n<li><strong>Synchro translation</strong> - 80 euro per hour</li>\r\n<li><strong>Written traslation</strong> - rate depend on the text subject</li>\r\n</ul>','Interpreter','Interpreter','Interpreter');
